import { render } from '@testing-library/react';
import { useEffect, useState } from 'react';
import bulkImages from './bulkImages';


let initialLists = [
    {imgName: 'ann', clicked: false},
    {imgName: 'arisu', clicked: false},
    {imgName: 'chishiya', clicked: false},
    {imgName: 'chota', clicked: false},
    {imgName: 'karube', clicked: false},
    {imgName: 'kuina', clicked: false},
    {imgName: 'kuzuryu', clicked: false},
    {imgName: 'kyuma', clicked: false},
    {imgName: 'lastboss', clicked: false},
    {imgName: 'niragi', clicked: false},
    {imgName: 'tatta', clicked: false},
    {imgName: 'usagi', clicked: false},
]
const Images = () => {
    const [click, setClick] = useState(initialLists)


    function shuffle(array) {
        let currentIndex = array.length, randomIndex;

        // While there remain elements to shuffle.
        while (currentIndex !== 0) {

            // Pick a remaining element.
            randomIndex = Math.floor(Math.random() * currentIndex);
            currentIndex--;

            // And swap it with the current element.
            [array[currentIndex], array[randomIndex]] = [
                array[randomIndex], array[currentIndex]];
        }

        return array;
    }


    function handleImgClick(e, indexOfImg) {
        console.log(click);
        const imgSrc = e.target.src;
        console.log(indexOfImg);
        const imgName = imgSrc.substring(imgSrc.lastIndexOf('/') + 1, imgSrc.indexOf('.'))
        let updatedImg = click.map((img) => {
            if (img.imgName === imgName) {
                return {...img, clicked: true};
            }
            return img;
        });
        console.log('updatedimg: ', updatedImg)
        setClick(updatedImg)
        console.log(click);

    }
    return (
        <div className='main'>
            {bulkImages.map((img, index) => (
                <img onClick={(e, index) => handleImgClick(e, index)} id={'img' + index} key={img} src={img} alt={img} className="bulkImage" />
            ))}
        </div>
    )
}

export default Images;
